<script>
/* eslint-disable */
import wenzhouMap from "../geoJson/WenZhou";
import fgqy from "./sfChart/fgqy";
import fgyg from "./sfChart/fgyg";
import fgTop from "./fgTop";
import { mapdata } from "../mapdata";
import { mapState, mapActions } from "vuex";

export default {
  data() {
    return {
      chart: undefined,
      mapdata,
      num: [
        
      ],
      staticNum: [0, 0, 0, 0],
      fixed_yg: { name: [], hb: [], rest: [] },
      fixed_qy: { name: [], hb: [], rest: [] },
      objData:[],
      date: "",
      time: "",
      date:"",
      logoshow: false,
      down: require("./img/down.png"),
      snfg: [0, 0, 0]
    };
  },
  components: { fgqy, fgyg, fgTop },
  computed: {
    ...mapState({
      FgfcList: state => state.FgfcList
    })
  },
  watch: {
    FgfcList() {
      this.fgfcDataFix();
    }
  },
  mounted() {
    var date = new Date();
    //var strdate = new Date(value.createtime.replace(/-/g, '/'))
    var year = date.getFullYear();
    var month = date.getMonth()<9?"0"+(date.getMonth()+1):date.getMonth()+1;
    var day = date.getDate()<10?"0"+date.getDate():date.getDate();
    var hour = date.getHours()<10?"0"+date.getHours():date.getHours();
    this.date = year+"年"+month+"月"+day+"日"+hour+"时"
    this.NYJJMapInit(); //调用地图
    !this.FgfcList.length && this.fetchFgfcList();
    //this.fgfcDataFix();

  },
  methods: {
    ...mapActions(["fetchFgfcList"]),
    
    fgfcDataFix() {
      if (!this.FgfcList.length) return;
      // this.date = this.FgfcList[0].gxsj.substring(8, 10);
      // this.time = this.FgfcList[0].gxsj.substring(11, 16);
      // this.$parent.sfdate = this.FgfcList[0].gxsj.substring(8, 10);
      // this.$parent.sftime = this.FgfcList[0].gxsj.substring(11, 16);
      const mapqyObj = {};
      const mapygObj = {};
      const mapyzg={};
      const mapwzg={};
      const qyObj = {};
      const ygObj = {};
      const num = [0, 0];
      const staticNum = [0, 0, 0, 0];
      this.FgfcList.map(item => {
        const _xq_ = item.area1.replace(/产业集聚区/g, "");
        //  头部
        // num[0] += parseInt(item.ysq_qy_cnt); //  复工申请企业数
        // num[1] += parseInt(item.ysq_snfgrs_cnt) + parseInt(item.ysq_swfgrs_cnt); //  复工申请员工数(市内市外)
        // //市内健康码统计
        // this.snfg[0] += parseInt(item.ysq_snfg_green_cnt);
        // this.snfg[1] += parseInt(item.ysq_snfg_yellow_cnt);
        // this.snfg[2] += parseInt(item.ysq_snfg_red_cnt);
        // //  地图统计
        // staticNum[0] += parseInt(item.ysq_gsqy_cnt);
        // staticNum[1] += parseInt(item.ysq_tzyygc_cnt);
        // staticNum[2] += parseInt(item.ysq_snfgrs_cnt);
        // staticNum[3] += parseInt(item.ysq_swfgrs_cnt);
        //  地图
        // !mapqyObj[_xq_] && (mapqyObj[_xq_] = 0);
        // !mapygObj[_xq_] && (mapygObj[_xq_] = 0);
        !mapyzg[_xq_] && (mapyzg[_xq_] = 0);
        !mapwzg[_xq_] && (mapwzg[_xq_] = 0);
        mapyzg[_xq_] +=parseInt(item.Sumyzg);
        mapwzg[_xq_] +=parseInt(item.Sumwzg);
        mapqyObj[_xq_] =mapwzg[_xq_]<=0?0:parseInt((mapyzg[_xq_]/mapwzg[_xq_])*100); //(mapyzg[_xq_]+mapwzg[_xq_])<=0?0:mapyzg[_xq_]/(mapyzg[_xq_]+mapwzg[_xq_])
        // mapygObj[_xq_] +=
        //   parseInt(item.ysq_snfgrs_cnt) + parseInt(item.ysq_swfgrs_cnt);
        // //  企业分析
        // if (["市本级", "其他区县"].indexOf(_xq_) < 0) {
          !qyObj[_xq_] &&
            (qyObj[_xq_] = { name: _xq_, Sumyzg: 0, Sumwzg: 0, rate: 0, rest: 0 });
          qyObj[_xq_].Sumyzg =mapyzg[_xq_];
          qyObj[_xq_].Sumwzg =  mapwzg[_xq_];
          qyObj[_xq_].rate = mapqyObj[_xq_];
          // qyObj[_xq_].rest +=
          //   parseInt(item.ysq_qy_cnt) -
          //   parseInt(item.ysq_tzyygc_cnt) -
          //   parseInt(item.ysq_gsqy_cnt);
        //   //  员工分析
        //   !ygObj[_xq_] && (ygObj[_xq_] = { name: _xq_, all: 0, sn: 0, sw: 0 });
        //   ygObj[_xq_].sn += parseInt(item.ysq_snfgrs_cnt);
        //   ygObj[_xq_].sw += parseInt(item.ysq_swfgrs_cnt);
        //   ygObj[_xq_].all +=
        //     parseInt(item.ysq_snfgrs_cnt) + parseInt(item.ysq_swfgrs_cnt);
        // }
      });
      //  数据处理
      const _mapdata_ = this.$util.clone(this.mapdata).map(item => {
        return {
          ...item,
          value: mapyzg[item.name] || 0,
          value2: mapwzg[item.name] || 0,
          value3: mapqyObj[item.name] || 0,
        };
      });
      const _qy_ = [];
      const fixed_qy = { name: [], Sumyzg: [], Sumwzg: [], rate: [], rest: [] };
      for (let v in qyObj) {
        if(v!="")
        {
          _qy_.push(qyObj[v]);
        } 
      }
      _qy_
        .sort(this.$util.compare("rate"))
        .reverse()
        .map(({ name, Sumyzg, Sumwzg, rate }) => {
          fixed_qy.name.push(name);
          fixed_qy.Sumyzg.push(Sumyzg);
          fixed_qy.Sumwzg.push(Sumwzg);
          fixed_qy.rate.push(rate);
          //fixed_qy.all.push(all);
        });
      const _yg_ = [];
      const fixed_yg = { name: [], all: [], sn: [], sw: [] };
      for (let v in ygObj) {
        _yg_.push(ygObj[v]);
      }
      _yg_
        .sort(this.$util.compare("all"))
        .reverse()
        .map(({ name, sn, sw, all }) => {
          fixed_yg.name.push(name);
          fixed_yg.sn.push(sn);
          fixed_yg.sw.push(sw);
          fixed_yg.all.push(all);
        });
      this.num = this.num.map((item, index) => {
        return { ...item, value: num[index] };
      });
      this.staticNum = staticNum;
      this.fixed_qy = fixed_qy;
      this.fixed_yg = fixed_yg;
      this.mapdata = _mapdata_;
      this.$nextTick(() => {
        this.NYJJMap(); //调用地图
        this.$refs.fg_chart.drawEchart();
        this.$refs.qf_chart.drawEchart();
      });
    },
    NYJJMapInit() {
      this.chart = this.$echarts.init(document.getElementById("nyjj-map"));
      this.$echarts.registerMap("wenzhou", wenzhouMap);
    },
    NYJJMap() {
      const that = this;
      const upurl =
        "image://data:image/png;base64,";
      const downurl =
        "image://data:image/png;base64,";

      this.chart.setOption({
        geo: {
          map: "wenzhou",
          zoom: 1.28,
          label: {
            normal: {
              show: false
            },
            emphasis: {
              show: false
            }
          },
          itemStyle: {
            shadowBlur: 20,
            shadowColor: "#000"
          }
        },
        // visualMap: {
        //   show: false,
        //   min: 1000,
        //   max: 100000,
        //   inRange: {
        //     color: [
        //       "rgb(230, 241, 216)",
        //       "rgb(200, 226, 177)",
        //       "rgb(172, 212, 74)",
        //       "rgb(104, 156, 32)"
        //     ]
        //   }
        // },
        series: [
          {
            type: "map",
            map: "wenzhou",
            zoom: 1.28,
            label: {
              normal: {
                show: false
              },
              emphasis: {
                show: false
              }
            },
            data: this.mapdata.map(item => {
              console.log(item.value3);
              return {
                name: item.name,
                value: item.Sumyzg,
                value2:item.Sumwzg,
                value3:item.rate,
                //qyvalue: item.value,
                itemStyle: {
                  // color: item.color || "#fff"
                  // 根据备案员工人数判断颜色
                  color:
                    item.value3>= 80
                      ? '#31A5F2'//"#689c20"
                      : item.value3>=50
                      ? "#63F856"
                      : item.value3>=20
                      ? "#ff912f"
                      : "#f82727"
                },
                coord: item.coord
              };
            }),
            markPoint: {
              // symbol: upurl,
              symbol: function(params, { name }) {
                return name == "浙南" || name == "龙湾区" ? downurl : upurl;
              },
              symbolSize: [84, 40],
              label: {
                normal: {
                  show: true,
                  offset: [0, 0],
                  //     offset: function(params, { name }) {
                  //   return name == "浙南" || name == "龙湾区" ? [0,1] : [0,-1];
                  // },
                  textStyle: {
                    color: "#000"
                  },
                  position: "inside",
                  align: "center",
                  formatter: function(params) {
                    return params.data.name != "龙湾区" ||
                      params.data.name != "浙南"
                      ? [
                          "{title|" +
                            params.data.name
                              .replace("县", "")
                              .replace("区", "")
                              .replace("市", "") +
                            "}",
                          "{num1|" +
                            params.data.value +
                            "}/{num2|" +
                            params.data.value2 +
                            "}/{num3|"+
                            params.data.value3+'%'+
                            "}"
                        ].join("\n")
                      : [
                          "{title1|" +
                            params.data.name
                              .replace("县", "")
                              .replace("区", "")
                              .replace("市", "") +
                            "}",
                          "{num3|" +
                            params.data.value +
                            "}/{num4|" +
                            params.data.ygvalue +
                            "}"
                        ].join("\n");
                  },
                  rich: {
                    title: {
                      color: "black",
                      fontSize: 10,
                      fontWeight: "bold",
                      offset: [0, 1]
                    },
                    num1: {
                      color: "#008aff",   
                      fontSize: 12,
                      fontWeight: "bold",
                      offset: [0, 1]
                    },
                    num2: {
                      color: "#32941b",
                      fontSize: 12,
                      fontWeight: "bold",
                      offset: [0, 1]
                    },
                    title1: {
                      color: "black",
                      fontSize: 12,
                      fontWeight: "bold",
                      offset: [0, 5]
                    },
                    num3: {
                      color: "#ffbf13",
                      fontSize: 12,
                      fontWeight: "bold",
                      offset: [0, 5]
                    },
                    num4: {
                      color: "#FF4242",
                      fontSize: 12,
                      fontWeight: "bold",
                      offset: [0, 5]
                    },
                    e: {
                      color: "#FF4242",
                      fontSize: 12,
                      fontWeight: "bold"
                    }
                  }
                }
              },
              data: this.mapdata
            }
          }
        ]
      });
      that.chart.getZr().on("click", function(event) {
        if (event.target) {
          that.$router.push({
            path: "/fgDetail",
            query: {
              label: that.mapdata[event.target.dataIndex].name,
              date: that.date,
              time: that.time
            }
          });
        }
      });
    },
    showLogo() {
      this.logoshow = true;
      const that = this;
      setTimeout(function() {
        that.logoshow = false;
      }, 3000);
    }
  }
};
</script>

